float numColumns = 10.0;
float numRows = 10.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 whiteColor = vec4(1.0,1.0,1.0,1.0);
    vec4 blackColor = vec4(0.0,0.0,0.0,1.0);

    float columns = mod(floor(uv.x * numColumns), 2.0); //used to determine even numbered spaces in x direction
    float rows = mod(floor(uv.y * numRows), 2.0); //used to determine even numbered spaces in y direction

    if(columns == 0.0 && rows == 0.0) //checks both columns and rows to see if it is even. Makes the square white
    {
    	fragColor = whiteColor;
    }
    else if (columns != 0.0 && rows != 0.0) //checks both columns and rows to see if both of them are not even. Makes the square white
    {
        fragColor = whiteColor;
    }
    else //whenever columns is even and rows isn't, or vice versa. Makes square black
    {
        fragColor = blackColor; 
    }
}
