// 1. No noise

// 2. Simple random "bad" noise; second coordinate via re-hashing

// 3. Blue noise generated by https://github.com/bartwronski/BlueNoiseGenerator/ single float; second coordinate via re-hashing

// 4. Blue noise generated by https://github.com/bartwronski/BlueNoiseGenerator/ 2 component vector

// 5. High-pass treated normal noise by Timothy Lottes https://www.shadertoy.com/view/4sBSDW 
// disclaimer - I didn't fully understand the implementation, so noise is shifted... Will try to fix it later.

// 6. 16x16 Bayer matrix by patapom https://www.shadertoy.com/view/Mlt3z8 ; second coordinate via re-hashing.

// 7. Interleaved Gradient noise by Jorge Jimenez http://advances.realtimerendering.com/s2014/index.html ; second coordinate via re-hashing.

// 8. Triangle Noise by Tom Forsyth https://www.shadertoy.com/view/4t2SDh

#define F1 float
#define F2 vec2
#define F3 vec3
#define F4 vec4

float rand(float n){return fract(sin(n) * 43758.5453123);}
float nrand( vec2 n ){return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);}
F1 Noise(F2 n,F1 x){n+=x;return fract(sin(dot(n.xy,F2(12.9898, 78.233)))*43758.5453);}

// Step 1 in generation of the dither source texture.
F1 Step1(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-12.0,t=1.0;   
 return (1.0/(a*4.0+b*4.0-c))*(
  Noise(uv+F2(-1.0,-1.0)*t,n)*a+
  Noise(uv+F2( 0.0,-1.0)*t,n)*b+
  Noise(uv+F2( 1.0,-1.0)*t,n)*a+
  Noise(uv+F2(-1.0, 0.0)*t,n)*b+
  Noise(uv+F2( 0.0, 0.0)*t,n)*c+
  Noise(uv+F2( 1.0, 0.0)*t,n)*b+
  Noise(uv+F2(-1.0, 1.0)*t,n)*a+
  Noise(uv+F2( 0.0, 1.0)*t,n)*b+
  Noise(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

// Step 2 in generation of the dither source texture.
F1 Step2(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-2.0,t=1.0;   
 return (4.0/(a*4.0+b*4.0-c))*(
  Step1(uv+F2(-1.0,-1.0)*t,n)*a+
  Step1(uv+F2( 0.0,-1.0)*t,n)*b+
  Step1(uv+F2( 1.0,-1.0)*t,n)*a+
  Step1(uv+F2(-1.0, 0.0)*t,n)*b+
  Step1(uv+F2( 0.0, 0.0)*t,n)*c+
  Step1(uv+F2( 1.0, 0.0)*t,n)*b+
  Step1(uv+F2(-1.0, 1.0)*t,n)*a+
  Step1(uv+F2( 0.0, 1.0)*t,n)*b+
  Step1(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

bool sampleDist(vec2 uv)
{
    float radius = 0.1;
    return sqrt(dot(uv, uv)) < radius;
}

bool sampleDistComp(vec2 uv, float radius_)
{
    float threshold = 0.005;
    float radius = 0.1 + radius_;

    return sqrt(dot(uv, uv)) < radius*(1.0+threshold) && sqrt(dot(uv, uv)) > radius * (1.0-threshold);
}

float interleavedGradientNoise(vec2 pos)
{
  float f = 0.06711056 * pos.x + 0.00583715 * pos.y;
  return fract(52.9829189 * fract(f));
}

float B2( vec2 _P ) {
//    return ((_P.y << 1) + _P.x + 1) & 3;    <= This would fare much better than modulos and floors :(
    return mod( 2.0*_P.y + _P.x + 1.0, 4.0 );
}

// Generates the 16x16 matrix
float B16( vec2 _P ) {
    vec2    P1 = mod( _P, 2.0 );                    // (P >> 0) & 1
    vec2    P2 = floor( 0.5 * mod( _P, 4.0 ) );        // (P >> 1) & 1
    vec2    P4 = floor( 0.25 * mod( _P, 8.0 ) );    // (P >> 2) & 1
    vec2    P8 = floor( 0.125 * mod( _P, 16.0 ) );    // (P >> 3) & 1
    return 4.0*(4.0*(4.0*B2(P1) + B2(P2)) + B2(P4)) + B2(P8);
}

float triangleNoise( vec2 n )
{
	float nrnd0 = nrand( n );
    // Convert uniform distribution into triangle-shaped distribution.
    float orig = nrnd0*2.0-1.0;
    nrnd0 = orig*inversesqrt(abs(orig));
    nrnd0 = max(-1.0,nrnd0); // Nerf the NaN generated by 0*rsqrt(0). Thanks @FioraAeterna!
    nrnd0 = nrnd0-sign(orig)+0.5;
	return (nrnd0 + 0.5) * 0.5;
}

