void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uvMod = fragCoord.xy / iResolution.xy;
    uvMod -= 0.5;
    uvMod.y *= iResolution.y / iResolution.x;
    uvMod.y += 0.05;
	vec2 fragCoordRescale = fragCoord.xy * 0.25 - fract(fragCoord.xy * 0.25);
    vec2 fragCoordMod = mod(fragCoordRescale.xy, 16.0);

    float angle = 0.0;
    float r = 0.0;
    float time = fract(iTime/16.0)*16.0;

    if (time > 2.0)
    {
        angle = Noise(fragCoordRescale.xy, 0.0);
        r = rand(angle);
    }
    if (time > 4.0)
    {
        angle = randBlue(fragCoordMod.xy);
        r = rand(angle);
    }
    if (time > 6.0)
    {
        vec2 b2 = randBlue2(fragCoordMod.xy);
        angle = b2.x;
        r = b2.y;
    }
    if (time > 8.0)
    {    
        angle = Step2(fragCoordMod.xy,0.0)+0.5;
        r = Step2(fragCoordMod.xy,0.11)+0.5;
    }
	if (time > 10.0)
    {
        angle = B16(fragCoordMod.xy)/ 256.0; 
        r = rand(angle);
    }
    if (time > 12.0)
    {
        angle = interleavedGradientNoise(fragCoordRescale.xy);
        r = rand(angle);
    }
    if (time > 14.0)
    {
     	angle = triangleNoise(fragCoordMod.xy);
        r = rand(angle);
    }

    float s = sin(angle * 2.0 * 3.1415926);
    float c = cos(angle * 2.0 * 3.1415926);

    vec4 col = sampleDist(uvMod + vec2(s,c) * sqrt(r) * 0.1) ? vec4(1.0) : vec4(0.0);

    col += sampleDistComp(uvMod, 0.0) ? vec4(1.0,0.0,0.0,0.0) : vec4(0.0);
    col += sampleDistComp(uvMod, 0.1) ? vec4(0.0,1.0,0.0,0.0) : vec4(0.0);

    if (fragCoord.y / iResolution.y > 0.8)
    {
        col = vec4(angle, r, 0, 0);
    }

    fragColor = col;
}
