void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // затравка
    float shift = sin(iTime*2.0) * 1.25;
    float q = cos(iTime)*50.0 + iResolution.y*25.0;

    // смешение, чтоб центр был в центре
	vec2 uv = ((fragCoord.xy + vec2(-iResolution.x/2.0,-iResolution.y/2.0)) / iResolution.xy);

    // считаем пиксель
    float blue = sin(uv.x*q) - cos(uv.y*q) + shift + 0.1;
    float green = sin(uv.x*q + 0.5) - cos(uv.y*q - 0.5) + shift;
    float red = sin(uv.x*q - 0.5) - cos(uv.y*q + 0.5) + shift;

    // красим пиксель
	fragColor = vec4(red,green,blue,0.0);
}
