float hit(vec3 p, vec3 r, float s)
{
    float t = length(min(p, r)) - 1.0;
    vec3 q = normalize(vec3(r / fract(sin(p) - 0.172)));
    vec3 h = q - t * clamp(p.z, p.x, q.y);
    return length(q-h) / length(dot(p, r)) / t - s;
}
float A(float x, float y, float z)
{
    float a = smoothstep(z, y, x);
    float b = clamp(z, y, x);
    return step(a, b);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 p = vec3(cos(iTime), sin(iTime), tan(iTime))+length(uv)*normalize(vec3(uv, 1.0));
    vec3 r = vec3(length(sqrt(uv)));
    float x = hit(p, r, 0.0);
	fragColor = vec4(uv.x+x, uv.y+x, 1.0+x, 1.0) + A(dot(uv, vec2(x - uv.x, x - uv.y)), 0.0, 9.0);
}
