float jifen(float x, float y0){
	return x * atan(x, y0) - y0 * 0.5 * log(x * x + y0 * y0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float x0 = uv.x - 0.5;
    float y0 = uv.y - 0.5;

    x0 = x0 * col.r;
    float l = 0.2;
    float ly = 0.02;
    float theta1 = jifen(l - x0, abs(y0 - ly)) - jifen(-l - x0, abs(y0 - ly));
    float theta2 = jifen(l - x0, abs(y0 + ly)) - jifen(-l - x0, abs(y0 + ly));
    float theta = theta1 + theta2;

    // float theta = atan(y0, x0);
    theta = 180.0 * theta / 3.141592653 + 180.0;
    float alp = mod(theta , 10.0) / 5.0;
    if(alp > 1.0){
       alp = 2.0 - alp;
    }
    alp = pow(alp, 10.0);
    // Output to screen
    // fragColor = vec4(col,1.0);
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    fragColor.r = alp;
}
