void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 coord = fragCoord / iResolution.xy;

	vec2 mouse = vec2(iMouse.x / iResolution.x, iMouse.y / iResolution.y);

    vec4 color = texture(iChannel0, coord);

    float radius = abs(sin(iTime));
    float borderWidth = 0.1;



    if(length(mouse - coord) - radius < radius && length(mouse-coord) - radius > radius - borderWidth)
    {
        color = texture(iChannel0, vec2(coord.x * 1.01, coord.y * 1.01));
    }

    fragColor = color;

}
