// see animated version here: https://www.shadertoy.com/view/ltGXRK

/**/ // --- 422 chars

#define S(v)   O += smoothstep(.04, .025, v) 

void mainImage( out vec4 O, vec2 U )
{
	U = (U+U -(O.xy=iResolution.xy)) / O.y;
    mat2 R = mat2(.9,-.5,.5,.9);
	O = vec4(1,0,0,1);

    S( abs(length(vec2(U.x,abs(U.y)+.7))    -1.));      // body

    U.x = abs(U.x);                                     // horizontal symmetry
    float l = length(U-vec2(.2,.55));

    U.y>.3 && U.y<.45 ? S( abs(U.x*.55-U.y*.2)*1.5  )   // eyes
                      : S(l), S(abs(l-.1));

#define C(a,b,f,d)  U.x>a && U.x<b ? S( abs( .08*sin(13.*U.x+f*1.57) -U.y + d) ) : O ; U *= R;
    C(.7, 1.4 , 2., .0  );                              // 3 tentacles
    C(.55,1.25, 3., .2  );
    C(.4, 1.  , 0., .25 );
} 
