/** // --- 442 chars

#define S(v)     O += smoothstep(.04, .025, v)
#define L(f,d)   S( abs( .08*sin(13.*U.x+f*1.57) - U.y + d) )

void mainImage( out vec4 O, vec2 U )
{
	U = (U+U -(O.xy=iResolution.xy)) / O.y;
	O = vec4(1,0,0,1);

    S( abs(length(vec2(U.x,abs(U.y)+.7))    -1.));         // body

    U.x = abs(U.x);                                        // horizontal symmetry
    float l = length(U-vec2(.2,.55));
    S(l) ; S(abs(l-.1));                                   // eyes
    U.y>.3 && U.y<.45 ? S( abs(U.x*.55-U.y*.2)*1.5  ) : O; 

    mat2 R = mat2(.9,-.5,.5,.9);
              U.x>.7  && U.x<1.4  ?  L(2., .0  ) : O;       // 3 tentacles
    U *= R;   U.x>.55 && U.x<1.25 ?  L(3., .2  ) : O;
    U *= R;   U.x>.4  && U.x<1.   ?  L(0., .25 ) : O;
} 
