/** // --- 498 / 477 chars

#define S(v) O += smoothstep(.04, .025, v)

void mainImage( out vec4 O, vec2 U )
{
	U = (U+U -(O.xy=iResolution.xy)) / O.y;
	O = vec4(1,0,0,1);

    S( abs(length(vec2(U.x,abs(U.y)+.7))    -1.));
    U.x = abs(U.x);
    S( abs(length(U-vec2(.2,.55))-.1));
    S(     length(U-vec2(.2,.55))    );
    U.y>.3 && U.y<.45 ?    S( abs(U.x*.55-U.y*.2)*1.5  ) : O; 

    U.x>.7 && U.x<1.4 ?    S( abs  (-.08*sin(13.*U.x) - U.y   ) ) : O;
    float a = .5, c=cos(a),s=sin(a);
    //U *= mat2(.9,-.5,.5,.9);
    U *= mat2(c,-s,s,c);
    U.x>.55 && U.x<1.25 ?  S( abs  (-.08*cos(13.*U.x) - U.y+.2) ) : O;
    //U *= mat2(.9,-.5,.5,.9);
    U *= mat2(c,-s,s,c);
    U.x>.4 && U.x<1. ?     S( abs  (.08*sin(13.*U.x) - U.y+.25) ) : O;
} 
