#define rnd(U) fract(1e5*sin(dot(U,vec2(1,77.321))))

#define T(i,j) texture(iChannel0, fract( ( U +vec2(i,j) ) / iResolution.xy) )

void mainImage( out vec4 O,  vec2 U )
{
    //if (iFrame==0) { O = vec4(-1e3,0,0,0); return; }

    O = T(0,0);
    if (O.x > 0.) return;

	O.x = max( max( max ( T(-1, 1) , T( 0, 1) ),
                    max ( T( 1, 1) , T(-1, 0) ) ),
               max( max ( T( 1, 0) , T(-1,-1) ),
                    max ( T( 0,-1) , T( 1,-1) ) ) ).x; 

    if (O.y < .9)
	O.y = ( T(-1, 1) + T(0, 1) + T(1, 1) 
          + T(-1, 0) + T(0, 0) + T(1, 0) 
          + T(-1,-1) + T(0,-1) + T(1,-1) ).y / 9.; 

    U -= 9.*iTime;
    if (O.x ==0. && rnd(U)>.9992) O = vec4(rnd(U+.1),1,1,0);
}
