void mainImage( out vec4 O, vec2 U )
{
	vec2  V = floor( U *= 10./ iResolution.y );
    float t = iDate.w, 
          T = mod(floor(t),2.),
          l = length(U-5.);
          t = smoothstep(.2,.8,fract(t));


    T<1. ? U.x += (2.*mod(V.y,2.)-1.) *t
         : U.y += (2.*mod(V.x,2.)-1.) *t;
    V = floor( U );

    O = .5 + .5* sin( iDate.w+ .2*l + 3.14*mod(V.x+V.y+T,2.) + vec4(0,2.1,-2.1,0) );
}
