void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord.xy / iResolution.xy;
    float t = iTime*0.01;
    float s = .3;
    float n = texture(iChannel1,u+vec2(t,t)).r;
    float a = s*length(u*2.-vec2(1.)) * n;
    mat2 m = mat2(cos(a),0,0,cos(a));

	fragColor = texture(iChannel0,mod(m*u,vec2(1.))); 
}
