void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //Get the texture color
    vec4 image = texture(iChannel0, uv);

    //Make a simple sine wave stripe
    float mask_pattern = 0.5 * sin(8.0 * uv.x - iTime) + 0.5;
    vec4 mask = mask_pattern * vec4(1.0);

    //Absolute difference blend mode as described at
    //https://docs.gimp.org/en/gimp-concepts-layer-modes.html
    vec4 difference = abs(mask - image);

	fragColor = difference;
}
