// just an application of https://www.shadertoy.com/view/XtG3WK

// --- tracking a grouped object using the moments in the image


void mainImage( out vec4 O,  vec2 U )
{
    vec2  R = iResolution.xy,
          T = texture(iChannel0,.5/R).zw*1024., // source stored resolution
          H = R/T,
          V = texture(iChannel1, 1.5/R).zw,    // average, M2_xy
          M = texture(iChannel1, .5/R).zx/V.x, // barycenter of shape
         M2 = texture(iChannel1, .5/R).wy/V.x,
          S = sqrt(M2-M*M);                      // std-dev of shape

    float r = V.y/V.x, z=1.;                     // M2_xy
    if (r==r)                                    // do nothing if undefined moments
    {
	  M2 = M2 - M*M; r = r - M.x*M.y;              // centered moments
    //mat2 cov = mat2(M2.x, r, r, M2.y);           // covariant matrix
      mat2 icov = mat2(M2.y, -r, -r, M2.x) / (M2.x*M2.y-r*r);  // inverse covariant matrix

      U /= T*min(H.x,H.y);                         // cf https://www.shadertoy.com/view/XlG3DW
      U -= M/T;                                    // centered coordinates
      z = dot(U, icov*U*T*T);                      // isodistance to center
      z = .5+.5*smoothstep(0.,5.,z);               // zoom: x2 inside to x1 outside
      U = z*U + M/T;
    }
    O = texture(iChannel0,U*T/R);              // fetch
    O /= z;                                      // glow
}

// --- source video


void mainImage( out vec4 O,  vec2 U )
{
    // store 1-1 pixels if enough room, or resample the video if small display
    vec2 R = min( vec2(640,360), iResolution.xy ); //iChannelResolution[0].xy; 
    O = texture(iChannel0,U/R);

    if (U==vec2(.5)) O.zw = R/1024.;               // stores resolution in O(.5)
}

// --- compute the moments of the character in the video (1er part)


#define I(U) max(0., texture(iChannel0,U).r - texture(iChannel0,U).g )
//#define I(U) texture(iChannel0,U).r / ( texture(iChannel0,U).g + 1e-3)

void mainImage( out vec4 O,  vec2 U )
{   
    vec2 R = iResolution.xy,
      // T = vec2(640,360); //iChannelResolution[0].xy;  
         T = texture(iChannel0,.5/iResolution.xy).zw*1024.; // source stored resolution
    O -= O;

    float x = U.x, y = U.y;

    if (y==.5 && x <= T.x) // O(x,.5) =  partial (vertical) M1 and M2 moments for y
        for (float y=.5; y<=360.; y++) {
            O.xy += vec2(y,y*y)*I(vec2(x,y)/R);
            if (y>T.y) break;
        }


    if (x==.5 && y <= T.y) // O (.5,y) = partial (horizontal) M1 and M2 moments for x
        for (float x=.5; x<=640.; x++) {
            O.zw += vec2(x,x*x)*I(vec2(x,y)/R);
            if (x>T.x) break;
        }


    if (x==1.5 && y>1. && y <= T.y+1.) // O(1.5,y+1) = partial (horizontal) sum of values
        for (float x=.5; x<=640.; x++) {
            O.z += I(vec2(x,y-1.)/R);
            if (x>T.x) break;
        }


	O /= T.yyxx;
}

// --- compute the moments of the character in the video (2nd part)

#define I(U) texture(iChannel0, U)

void mainImage( out vec4 O,  vec2 U )
{   
    vec2 R = iResolution.xy,
      // T = vec2(640,360); // iChannelResolution[0].xy;
         T =  texture(iChannel1,.5/R).zw*1024.; // source stored resolution
    float x = U.x, y = U.y;
    O -= O;

    if (U==vec2(.5)) 
    {
        for (float x=.5; x<=640.; x++) {   // M1 and M2 moments for y
            O.xy += I(vec2(x,y)/R).xy;
            if (x>T.x) break;
        }


        for (float y=.5; y<=360.; y++) {    // M1 and M2 moments for x
            O.zw += I(vec2(x,y)/R).zw;
            if (y>T.y) break;
        }

        O /= T.xxyy;
    }

    if (U==vec2(1.5)) 
    {
        for (float y=.5; y<=360.; y++) {  
            O.z +=   I(vec2(x,y+1.)/R).z;  // sum of values
            O.w += y*I(vec2(x-1.,y)/R).z;  // M2_xy
            if (y>T.y) break;
        }

        O /= T.xxyy;
    } 
}
