precision mediump float;

vec3 softsaturate(in vec3 x, in vec3 lim, in float linPercent) {
    vec3 xN = abs(x / lim);
    vec3 lp2 = vec3(linPercent);
    vec3 k = log( (-lp2 - vec3(1.0)) / (lp2 - vec3(1.0)) ) / lp2;
    vec3 st = step(lp2, xN);

    //linear till linCut, logistic curve after
    return ((vec3(1.0) - st) * x) + (st * (vec3(2.0) / (vec3(1.0) + exp(-k * xN)) - vec3(1.0)) * abs(lim) * sign(x));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(uv.x * 5.);
    if (uv.y < 0.5) 
    	col = softsaturate(col, vec3(1.0), 0.8);
        //col = tanh(col);
   	else
        col = clamp(col, vec3(-1.0), vec3(1.0));


    // Output to screen
    //if (col.x >= 0.99999)
        //fragColor = vec4(0.0, 1.0, 0.0 ,1.0);
    //else
        fragColor = vec4(col ,1.0);
}
