vec4 t (vec2 v) {return texture(iChannel0,v/iResolution.xy);}
void mainImage( out vec4 C, in vec2 U )
{
    vec4 me = t(U);
    vec2 s = texture(iChannel1,U/iResolution.xy).xy;
    float f = length(s.xy);
    C = vec4(f*vec3(1,.5,.3),1);
    C += 3.*me.w*me.w;
    C.xyz += vec3(15,12,35)*me.w*length(me.xy);
    C *= 0.8+0.2*texture(iChannel2, reflect(vec3(0,0,1),normalize(vec3(s,.1))));

}

vec2 ur, U;
vec4 t (vec2 v, int a, int b) {return texture(iChannel0,((v+vec2(a,b))/ur));}
vec4 t (vec2 v) {return texture(iChannel0,(v/ur));}
float area (vec2 a, vec2 b, vec2 c) { // area formula of a triangle from edge lengths
    float A = length(b-c), B = length(c-a), C = length(a-b), s = 0.5*(A+B+C);
    return sqrt(s*(s-A)*(s-B)*(s-C));
}
void mainImage( out vec4 Co, in vec2 uu )
{
    U = uu;
    ur = iResolution.xy;
    if (iFrame < 1 ) {
        Co = vec4(0);
    } else {
        //init position and bounding square
        vec2 v = U;
        vec2 A = v + vec2( .5, .5),
             B = v + vec2( .5,-.5),
             C = v + vec2(-.5, .5),
             D = v + vec2(-.5,-.5);
        // advect position and bounding square backwards through time
        // so subtracting the velocity from the position to backsolve
        for (int i = 0; i < 5; i++) {
            v -= t(v).xy;
            A -= t(A).xy;
            B -= t(B).xy;
            C -= t(C).xy;
            D -= t(D).xy;
        }
        // get information about the new position
        vec4 me = t(v,0,0);
        vec4 n = t(v,0,1),
            e = t(v,1,0),
            s = t(v,0,-1),
            w = t(v,-1,0);
        // blend information with neighbors
        vec4 ne = .25*(n+e+s+w);
        me = mix(me,ne,vec4(0.04,0.04,1,0.0));
        // adjust pressure according to the change in the 
        // area of the bounding square
        float da = (area(A,B,C)+area(B,C,D)-1.);
        me.z -=  da;
        vec4 pr = vec4(e.z,w.z,n.z,s.z);
        me.xy = me.xy + vec2(pr.x-pr.y, pr.z-pr.w)/ur;
        // add a little thing in the middle
        vec2 o = abs(U-0.5*ur);
        if (iFrame<2&&max(o.x,o.y)<2.) me.w=1.;
        // make the borders frozen
        if (min(0.5*ur.x-o.x,0.5*ur.y-o.y)<2.) me.xy*= 0.;
        // add the electric force from the other buffer to the velocity
        me.xy += 0.0016*texture(iChannel1,v/ur).xy;

        Co = me;

    }
}

vec2 ur, U;
vec4 t (vec2 v) {return texture(iChannel0,(v/ur));}
vec2 st (float w, float u, vec2 v) {
    // this is kind of hard to read but its just like the classic
    // electric force formula you might learn in a physics class
    //
    // (possitive*possitive+negative*negative - negative*positive-positive*negative)/R
    return ( w*u-w*(1.-u)-(1.-w)*u+(1.-w)*(1.-u))*(v.xy)/dot(v,v);}
vec2 st (float p, float n, float p1, float n1, vec2 v) {
	return (p*p1+n*n1-p*n1-n*p1)*normalize(v)/dot(v,v);
}
void mainImage( out vec4 Co, in vec2 uu )
{
    U = uu;
    ur = iResolution.xy;
    if (iFrame < 1 ) {
        Co = vec4(0);
    } else {
        vec4 me = t(U);
    	vec2 sf = vec2(0);
        me.z+=1.;
        for (int x = -2; x <= 2; x++) {
        for (int y = -2; y <= 2; y++) {
            if (x==0&&y==0) continue;
            // sum electric force from 5x5 neighborhood
            //sf += st (me.w,t(U+vec2(x,y)).w,vec2(x,y));
            vec4 o = t(U+vec2(x,y));
            o.z+=1.;
            sf += st (me.w*me.z,(1.-me.w)*me.z,o.w*o.z,(1.-o.w)*o.z,vec2(x,y));
        }
        }
    	Co = vec4(sf,0,1);
    }
}

