void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float screenRatio = iResolution.x/iResolution.y;
    vec2 uv = fragCoord/iResolution.xy;

    vec2 panningUV = uv/5.0;
         panningUV.x += iTime/50.0;

    vec4 noise = texture(iChannel0,panningUV);
    noise*= 2.0;

    vec2 center = (iMouse.xy / iResolution.xy);
        center.x*=screenRatio;
    vec2 MouseUV = uv - (iMouse.xy / iResolution.xy) -0.5;


    vec2 correctedUV = uv;
    correctedUV.x *= screenRatio;

   //float s = texture(iChannel, uv * .5);  
    vec2 centeredUv = MouseUV;
   // centeredUv.y -= 0.5;
    centeredUv *= 2.0;
    centeredUv.y = mix(1.0-fract(centeredUv.y),fract(centeredUv.y),1.0-(smoothstep(fract(MouseUV.y),fract(MouseUV.y),0.5)))*2.0;
	centeredUv.x += 1.0;

    vec3 radial = (((atan(centeredUv.y,centeredUv.x)*vec3(1.0))+(3.1415926535897932384626433832795))/((3.1415926535897932384626433832795)*2.0));
	//radial.x +=(sin(iTime));
    float doubleRadial = 1.0-(clamp((radial.x*2.0),0.0,1.0));

    float circle = distance(vec2(.5,.5),uv);
    vec2 UV = vec2(radial.x,circle);    

    int tx = int(UV.x+2.0);
    float fft = texelFetch( iChannel1, ivec2(tx,0), 0 ).x;
    //vec3 Color = vec3( fft, 4.0*fft*(1.0-fft), 1.0-fft ) * fft;
    float GradSound = smoothstep(UV.y-.2,UV.y+.5,1.0- fft);





    float distanceCenter = distance(center,correctedUV)*(GradSound+(sin(iTime)+5.0)/2.0);

    vec3 col = vec3(0,0,0);
    col.xy = (floor((vec2(distanceCenter,distanceCenter)*10.0)+noise.x)/10.0);
    col.z = 1.0/col.x;

   fragColor = vec4(col*center.xyx,1.0);
   //fragColor = vec4(centeredUv,0.0,1.0);

}
