void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 pixelColor = texture(iChannel0, uv.xy);
   	float distanceToMouse = distance(iMouse.xy, fragCoord.xy);
    float lightIntencive = ( 1.0 - distanceToMouse / 200.0 );

    fragColor = pixelColor * lightIntencive;
}
