{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define RADIUS 10.0
#define PI 3.1415926

#define dA 1.0
#define dB 0.5
#define feed 0.055
#define k 0.062
#define deltaTime 0.1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 nppd = 1.0/iResolution.xy * 1.0;

    vec2 nmp = iMouse.xy / iResolution.xy;

    vec3 c = vec3(0.0);

    //	laplacian
    vec3 tl = texture(iChannel1, abs(uv + nppd * vec2( -1.0, -1.0))).rgb * 0.05;
    vec3 tc = texture(iChannel1, abs(uv + nppd * vec2(  0.0, -1.0))).rgb * 0.20;
    vec3 tr = texture(iChannel1, abs(uv + nppd * vec2(  1.0, -1.0))).rgb * 0.05;
    vec3 ml = texture(iChannel1, abs(uv + nppd * vec2( -1.0,  0.0))).rgb * 0.20;
    vec3 mc = texture(iChannel1, abs(uv + nppd * vec2(  0.0,  0.0))).rgb * -1.0;
    vec3 mr = texture(iChannel1, abs(uv + nppd * vec2(  1.0,  0.0))).rgb * 0.20;
    vec3 bl = texture(iChannel1, abs(uv + nppd * vec2( -1.0,  1.0))).rgb * 0.05;
    vec3 bc = texture(iChannel1, abs(uv + nppd * vec2(	0.0,  1.0))).rgb * 0.20;
    vec3 br = texture(iChannel1, abs(uv + nppd * vec2(  1.0,  1.0))).rgb * 0.05;
    vec3 laplace = tl + tc + tr + ml + mc + mr + bl + bc + br;

    float laplaceA = laplace.r;
    float laplaceB = laplace.b;
    float lastA = mc.r;
    float lastB = mc.b;

    //	a is stored on r
    float newA = 	lastA + 
        			(	( dA * laplaceA ) - 
                     	( lastA * lastB * lastB ) +
                     	( feed * (1.0 - lastA) ) + 10.0		) * deltaTime;

    //	b is stored on g
    float newB = 	lastB + 
        			(	( dB * laplaceB ) + 
                     	( lastA * lastB * lastB ) -
                     	( (k + feed) * lastB )			) * deltaTime;


    fragColor = vec4( newA, 0.0, newB, 1.0 );

    float circle = step(length(iMouse.xy - fragCoord),RADIUS);
    if(iMouse.z > 0.0)
        //	place circle into b channel
    	fragColor = max(fragColor,vec4( fragColor.rg, circle, 1.00 ));
}
