vec2 uvMod(vec2 uv, float stretchX, float ratioY)
{
    float x = uv.x;
    float y = uv.y;

    x = x/stretchX;
    y = y + sin(iTime * (1.0 / ratioY)) * ((sin(iTime) + (ratioY/2.0)) / ratioY);

    return vec2(x, y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy);

    // Texture
    vec4 tex = texture(iChannel0, uvMod(uv, 1.0, 0.5));
    vec4 tex2 = texture(iChannel1, uvMod(uv, 20.0, 0.9));

    // Desaturate
    tex2.x = (tex2.x+tex2.y+tex2.z)/3.0;
    tex2.y = tex2.x;
    tex2.z = tex2.x;

    vec4 op = tex+tex2*0.5;

    // Output to screen
    fragColor = op;
}
