#define inputImageTexture iChannel0
#define inputImageTexture2 iChannel1
#define texture2D(a,b) texture(a,b)
#define gl_FragColor fragColor
#define textureCoordinate fragCoord.xy / iResolution.xy
#define textureCoordinate2 fragCoord.xy / iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    highp float t = fract(iTime);

    gl_FragColor = vec4(mix(texture2D(inputImageTexture, textureCoordinate), texture2D(inputImageTexture2, textureCoordinate2), t).rgb, 1.);
