void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 pxy = fragCoord/iResolution.xy;

    vec2 rxy = fragCoord;

    float r = sqrt(rxy.x*rxy.x+rxy.y*rxy.y)*3.14/iResolution.x;

    // Time varying pixel color
    vec3 col = vec3(0, 0.5+0.5*sin(r*r*iResolution.x+iTime*3.0), 1);

    // Output to screen
    fragColor = vec4(col,1.0);
}
