void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float magR = 1.0 / 16.0;
    float magG = 1.0 / 16.0;
    float magB = 1.0 / 16.0;

    vec4 shiftTex = texture( iChannel1, uv );
    vec2 shR = vec2( uv.x + (shiftTex.x * magR), uv.y + (shiftTex.x * magR) );
    vec2 shG = vec2( uv.x + (shiftTex.y * magG), uv.y + (shiftTex.y * magG) );
    vec2 shB = vec2( uv.x + (shiftTex.z * magB), uv.y + (shiftTex.z * magB) );


    vec4 result = vec4(0.0, 0.0, 0.0, 0.0);

    result.r = texture( iChannel0, -shR ).r;
    result.g = texture( iChannel0, -shG ).g;
    result.b = texture( iChannel0, -shB ).b;

	fragColor = result;
}
