float equation(float x, float y) {

    float result = 0.0;

    result = sin(x/100.0+iTime) * 70.0 + sin(x/10.0) * 5.0 + sin(iTime) * 10.0 + cos(x/60.0)*20.0 + sin(x/30.0+iTime*5.0)*20.0 - y + 80.0;

    //result = (x - 300.0) * (x - 300.0) + (y - 300.0) * (y - 300.0) - 100.0*100.0;

    return result;
}

