vec3 odot(vec2 uv, vec2 t,float osc){
	return( vec3(smoothstep(.1*osc,.15*osc ,length(uv-t))));
}

vec3 ring(vec2 uv, vec2 t,float r, float w){
	return(vec3(step(r,length(uv-t)) + step(length(uv-t),r-w)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ar = iResolution.y/iResolution.x;
	vec2 uv = fragCoord.xy/iResolution.xy;
	vec2 t = iMouse.xy/iResolution.xy;
	vec2 sqt = vec2(t.x, t.y *ar);
	vec2	squv = vec2(uv.x,uv.y*ar);
	float osc = abs(cos(iTime*.8));
	float oss = abs(sin(iTime*.8));
	//vec3 col = mix(odot(squv,sqt,osc),odot(squv,vec2(.5,.5*ar),osc),0.5);
	float boff =  .08;
	vec2 cent = vec2(.5, .5*ar);
	vec3 col = ring(squv-vec2(boff,.0),cent,.06,.004) * ring(squv+vec2(boff,0.0),cent,.06,.004);
	col *= ring(squv-vec2(boff-.002*cos(iTime*.8),.0),cent,.03,.002) *
    	ring(squv+vec2(boff-.002*cos(iTime*.8),0.0),cent,.03,.002);
	col *= ring(squv-vec2(boff-.001,.0),cent,.04,.004) * ring(squv+vec2(boff-.003,0.0),cent,.04,.004);
	fragColor = vec4( col, 1.0 );
}
