// Validation of IQ's https://iquilezles.org/articles/distance code example producing 
// shape https://iquilezles.org/articles/distance/gfx03.jpg

float f(vec2 p) // 
{
	float  r = length(p);float a = atan(p.y,p.x);
    return r-1.+0.5*sin(3.*a+2.*r*r);   
}

vec2 grad( in vec2 x )
{
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( f(x+h.xy) - f(x-h.xy),
                 f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}

float color( in vec2 x )
{
    float v = f( x );
    vec2  g = grad( x );
    float de = abs(v)/length(g);
    return smoothstep( 0.19, 0.20, de ); // IQ's original code - output https://i.imgur.com/11Zx9OK.png does not match IQ's image
//    return step(0.022, de ); // Fix - output https://i.imgur.com/scz6D9f.png does match IQ's image, except for central dots 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / iResolution.xy;
    uv *= 2.1; uv.x*=1.3; // resize to match orig dimensions
	fragColor = vec4(1.0-color(uv));
}
