#define TAU 6.28318530718

float smin(float a, float b, float k)
{
	float h = clamp(.5 + .5*(a-b)/k, 0., 1.);
	return mix(a, b, h) - k*h*(1.-h);
}

float fidget(vec2 uv, float t)
{
    float a = mod(atan(uv.x, uv.y)-t, TAU/3.) - TAU/6.;
    uv = vec2(sin(a),cos(a))*length(uv);

    float c0 = length(uv) + .01;
    float c1 = length(uv - vec2(0, .5));

    float d = 4. / iResolution.x;
    float y0 = smoothstep(-d, d, min(c0, c1) - .2);
    float y1 = smoothstep(-d, d, smin(c0, c1, .2) - .3);

    return (y0 + y1) / 2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy - iResolution.xy) / iResolution.y;
    float ditter = texelFetch(iChannel0, ivec2(fragCoord)%8, 0).r;
    float time = iTime - .01 * ditter;
    fragColor = vec4(fidget(uv, sqrt(4.+iMouse.x) * time)) - 0.1*dot(uv,uv);
}
