void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 st = fragCoord.xy/iResolution.xy;
float fft1  = texture( iChannel0, vec2(st.y,0.15) ).x; 
float fft2  = texture( iChannel0, vec2(st.y,0.25) ).x; 
float fft3  = texture( iChannel0, vec2(st.y,0.55) ).x; 

float pct = 0.0;
float f1=(1.0-fft1);
float f2=(1.0-fft2);
float f3=(1.0-fft3);
pct = distance(st,vec2(0.5));

vec3 color = 
   vec3(pow(st.y*st.y*1.6180+st.y*st.y*1.6180,f1*2.2),f1*pct*1.6180,0.2*pct+st.y/pct)
        *abs(
		 sin(
            st.y*1.6180*st.y*
            16.0*iTime*pct*
            12.0+1.6180*
            iTime*
            pct)*
            f1)*
    		(195.0508*f1*pct);

    fragColor = vec4(vec3(1.5) - color, 1.0 );
}
