void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float time = iTime; 
    float ratio = iResolution.x/iResolution.y; 
    float nCircles = 20.0;

    //toons
    vec2 uvc = uv*vec2(ratio, 1.0)-vec2(1.0, .50);
    float linFog = .80*length(uvc);
    float c0 = pow(.80*length(uvc), .250); //pow for perspective

    float ff0 =fract(c0*nCircles-time);
    float cf0 = (c0*nCircles-ff0)/nCircles;

    //colorize / shade / fog
    vec4 outer = vec4(213.0, 64.0, 51.0, 255.0)/255.0;
    vec4 inner = vec4(239.0, 136.0, 65.0, 255.0)/255.0;
    fragColor = mix(inner, outer, ff0);
    fragColor *= vec4(1.0-ff0*ff0*c0*.5); 
    fragColor = mix(inner*1.5,fragColor,  pow(c0, 4.0));

    //vignetting
    float v0 = 1.0-pow(.50*length(uvc), 2.9); //pow for perspective
  	fragColor *= vec4(v0*v0);

    //video overlay
    vec4 green = vec4(12.0, 162.0, 37.0, 255.0)/255.0; 
    vec4 vid = texture(iChannel0, uv);
    float dist =min(1.0, pow(length(green-vid), 1.310)*5.00);
    fragColor = mix(fragColor, vid, dist-fract(dist));
}
