void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    vec4 texColor = texture(iChannel0,uv);
    vec2 texStep = 2. / iResolution.xy;

    fragColor = vec4(texColor.xyz,1.0);
	vec4 s01 = texture(iChannel0,uv + vec2(1,0)*texStep);
	vec4 s02 = texture(iChannel0,uv + vec2(-1,0)*texStep);
	vec4 s03 = texture(iChannel0,uv + vec2(0,1)*texStep);
	vec4 s04 = texture(iChannel0,uv + vec2(0,-1)*texStep);

    vec4 grad1 = s02 - s01;
    vec4 grad2 = s04 - s03;
    vec4 avg = (grad1 + grad2)*0.5;

    fragColor = avg*10.0;
}
