void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    vec3 c = vec3(0,0,0);

 #define NUM_CIRCLES	3

    for (int i=0; i<NUM_CIRCLES; i++)
    {
//    iTime
   		float t = iTime + float(i)*(2.0 * 3.1415927/float(NUM_CIRCLES));
   		 vec2 centre = .2 * vec2(sin(t),cos(t));
    	float d = length(uv-vec2(0.9,0.5)+centre) + iTime + sin(iTime);

   		 vec4 tex = texture(iChannel0,vec2(d * 2.,0));
        c += tex.xyz;
    }
//    vec3 c = tex.xyz; //vec3(fract(d*11.),0,fract(d*10.));
	fragColor = vec4(c/float(NUM_CIRCLES),1.0);
}
