// Confetti2d - written by Jakob Thomsen
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

float refl(float v)
{
    return 1.0 - abs(fract(v / 2.0) * 2.0 - 1.0);
}

vec2 refl2(vec2 v)
{
    return 1.0 - abs(fract(v / 2.0) * 2.0 - 1.0);
}

float ball(vec2 uv, vec2 pos, float r)
{
    return step(length(uv - pos), r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    const int n = 2000;
    for(int i = 0; i < n; i++)
    {
        vec2 vel = texture(iChannel1, vec2(float(i) / float(n), 0.0)).xy;
        vec2 pos = texture(iChannel1, vec2(float(i) / float(n), 0.5)).xy;
        pos += vel * iTime * 0.1;
        vec2 uv = fragCoord.xy / iResolution.xy;
        vec3 color = texture(iChannel1, vec2(float(i) / float(n), 0.25)).rgb;
        color *= ball(uv, refl2(pos), 0.02);
        //fragColor.rgb += color; // ADD
        if(length(color) > 0.0)
            //fragColor.rgb = mix(color, fragColor.rgb, 0.5); // ALPHA
            fragColor.rgb = color; // REPLACE
    }
}
