void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2  px = 2.0*(-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

    float rot = .25 * iTime;
    float x = px.x; float y = px.y;
    px.x = cos(rot)*(x) + sin(rot)*(y);
    px.y = -sin(rot)*(x) + cos(rot)*(y);

    vec3  color = vec3(0.90,0.54,0.38);
    vec2  pa = smoothstep( 0.0, 0.2*abs(sin(iTime)), (cos(1.2*px) - 0.1) );

    fragColor = vec4( color*pa.y*pa.x, 1.0 );
}
