void mainImage( out vec4 C, in vec2 O )
{
    vec2 r = iResolution.xy;
	vec2 uv = O.xy / r.xy;
    uv = uv * 2. - 1.; 
    uv.x *= r.x / r.y;

    float a = atan(uv.x, uv.y) + iTime;
    float d = length(uv) + sin(cos(a * 10.) + a) * .1 + .1;
    a = iTime;

    vec3 c = vec3(sin(a), 
                  sin(a*3. + 3.*.2), 
                  sin(a*2.)) * .5 + .5;

    uv.x /= uv.y - 1.11;

	C = vec4(c / (smoothstep(1., 0., abs(mod(uv.x + .5, 1.)-.5)) * (d*d)), 1.);
}
