float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*4758.5453123);
}

float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv =(4.0*( fragCoord.xy / iResolution.xy ) - 2.0);

	float v=0.0;
	float ii=0.0;
	float rg=2.0;
	int f=0;

	for(int i=0;i<10;i++)
	{
		vec2 n1=rg*vec2(sin(iTime+ii),cos(iTime+ii));
		vec2 n2=rg*vec2(cos(iTime+ii),sin(iTime+ii));
		vec2 pos=vec2(noise(n1),noise(n2));
		float l1 = 1.5/length(pos - uv);
		if( f==0 )
		{
			v+=l1;
			f=1;
		}
		else
		{
			v-=l1;
			f=0;
		}
		ii+=1.0;
	}



    fragColor = vec4(v,v,v,1.0);
}
