void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x += sin(iTime + uv.y);
    int x = floatBitsToInt(sin(iTime / 3.0) * (uv.x + 1.0) / 2.0);
    int y = floatBitsToInt(cos(iTime/4.0) * (uv.y + 1.0) / 2.0);
    int n = 20;
    int stripeX = ((x & (1 << n)) >> n) % 2;
    int stripeY = ((y & (1 << n)) >> n) % 2;
    int f = (stripeX + stripeY) % 2;
    fragColor = vec4(f, tan(cos(iTime * 3.0)), (sin(uv.y * 10.0 * 6.28) + 1.0) / 2.0, 1.0);
}
