// image

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, abs(-0.5+fragCoord.xy/iResolution.xy));
}

// BufferA

vec4 getPixel(in vec2 xy)
{
  return texture(iChannel0, xy); 
}

vec4 getPixelBlurredB(in vec2 xy)
{  
  return 0.5*(getPixel(xy)+getPixel(xy + 1.1/iResolution.xy));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = 0.996 * fragCoord.xy / iResolution.xy; 
    vec4 c = getPixel (xy)            // Zoom in
       - getPixelBlurredB (xy) / 4.;  // Darken interior more than boundary
    c *= 1.25;                        // Brighten
    c -= 0.001;                       // Darken - dark more

    if (fragCoord.x == .5 && fragCoord.y == .5)  // initilize ?
      c = vec4(0.81, 0.4, 0.3, 0.);

    fragColor = clamp(c, -0.2, 1.); 
}
