void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // 4 way flip of texture coords
	vec2 uvA = fragCoord.xy / iResolution.xy;
    vec2 uvB = uvA;   uvB.x = 1.0 - uvB.x;
    vec2 uvC = uvA;   uvC.y = 1.0 - uvC.y;
    vec2 uvD = uvB;   uvD.y = 1.0 - uvD.y;

    // combine 4 texture samples
    vec4 c = texture(iChannel0, uvA) +
             texture(iChannel0, uvB) -
        	 texture(iChannel0, uvC) -
             texture(iChannel0, uvD);

    // Get microphone amp
    float v1 = 10.0*texture(iChannel2, vec2(0.1, 0.0)).x;
    float v2 = 10.0*texture(iChannel2, vec2(0.2, 0.0)).x;
    float v3 = 10.0*texture(iChannel2, vec2(0.4, 0.0)).x;

	fragColor = vec4(
        0.5 + 0.5 * sin(c.r*5.0+v1), 
        0.5 + 0.5 * sin(c.g*5.0+v2), 
        0.5 + 0.5 * sin(c.b*5.0+v3), 
        1.0);
}
