#define TAU 6.2831853071

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec3 hsv2rgb(vec3 color) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(color.xxx + K.xyz) * 6.0 - K.www);
    vec3 rgb = vec3(color.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), color.y));

    return rgb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= vec2(0.5);
    uv.x *= iResolution.x / iResolution.y;
    uv *= rotate2d(distance(uv, vec2(sin(iTime * 0.1))));
    uv += vec2(0.5);

    float angleX = TAU*distance(vec2(0.666), vec2(uv.x));
    float angleY = TAU*distance(vec2(0.333), vec2(uv.y));

    float g = (sin(angleX * cos(angleY)));
    float r = sin(sin(angleY * angleX) + iTime);

    fragColor = vec4( hsv2rgb( vec3( g * r * 0.23, 1.0 - g, r - g) ), 1.0 );
    fragColor += vec4( hsv2rgb( vec3( g + r * 0.23 + 0.5, g, 0.5 - (r / 3.0)) ), 1.0 );
    fragColor.rgb = sqrt(fragColor.rgb); 
}
