#define plain true       // false: half-pix offset
#define SRC   iChannel1  // 0: video 1: buffer

// ==== text utils

float char(vec2 p, int c) { // --- access to the image of ascii code c
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return 0.;
	return textureGrad( iChannel3, p/16. + fract( vec2(c, 15-c/16) / 16. ), 
                        dFdx(p/16.),dFdy(p/16.) ).x;
}
float pInt(vec2 p, float n) { // --- display uint4
    float v = 0.;
    for (float i = 3.; i>=0.; i--) 
        n /=  9.999999, // 10., // for windows :-(
        v += char(p - .5*vec2(i,0), 48+ int(fract(n)*10.) );
    return v;
}

// === texture MIPmap test

void mainImage( out vec4 O, vec2 U )
{
    vec2 R =  iResolution.xy,
         M = length(iMouse.xy)>10. ? iMouse.xy : R*vec2(.5+.5*cos(.5*iTime));
    O-=O;
  //U-=.5;
    float N = floor(log2(float(textureSize(SRC,0).x)))+1.;
    float z =  N*M.y/R.y;  int iz = int(z);
    vec2 S = vec2(textureSize(SRC,iz));
  //vec2 S = R / float(1<<iz);

    // --- Bottom-left: texelFetch LODs
    O +=  // texelFetch( iChannel0, ivec2(2.*U)>>iz , iz )      // raw
          plain ? texelFetch( SRC, ivec2(2.*U/R*S) , iz ) // remap ( no half-pixel)
                : texelFetch( SRC, ivec2(2.*(U-.5)/R*(S-1.)+.5) , iz ); // remap ( with half-pixel )

    // --- Top-Right: texture LODs
    if (U.x >= R.x/2. && U.y >= R.y/2.)
       O +=  plain ? textureLod( SRC,  2.*U/R-1., floor(z))             // raw
                   : textureLod( SRC, .5/S + 2.*(U-.5)/R-1., floor(z)); // with half-pixel

    // O = texelFetch( SRC, ivec2(U/R*S) , iz ) ; // full screen

    // --- display size
    O += pInt(( U/R.y - vec2(.27,.85)) * 8., float(iz));
    O += pInt(( U/R.y - vec2(.1 ,.7) ) * 8., float(S.x));
    O += pInt(( U/R.y - vec2(.45,.7) ) * 8., float(S.y));

    // --- slider
    O += smoothstep( .035, .03, length((U-.45*vec2(R.x,0))/R.y-vec2(0,floor(z)/9.)))
        * ( .7+.3*sin(18.*iTime));
}

