/**  // --- calibration target
void mainImage( out vec4 O , vec2 U ) {
    U /= iResolution.xy;

    O =   length( U-round(U)) < .1 ? vec4(round(U),0,0)
        : length( U-.5) < .1       ? vec4(1)
        : abs(fract(U.x*2.)-.4) < .05 ? vec4(1,0,0,0)
        :                            vec4(0,0,1,0);
}
