#define PI 3.14159265359
#define PI2 6.28318530718
#define PI_HALF 1.5707963267949

struct RadialGradient
{
    float radius;
    vec4 color;
    vec2 point;
};

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float w = iResolution.x / 2.;
    float h = iResolution.y / 2.;

    float fill = max(iResolution.x, iResolution.y);

    RadialGradient gradients[] = RadialGradient[](
		RadialGradient(1.0, vec4(.604,.816,1.00,.7), vec2(0.5 * w * sin(.13 * iTime - 0.44) + w, 0.5 * h * sin(.34 * iTime - 2.41) + h)),
        RadialGradient(1.0, vec4(.125,.310,.725,.7), vec2(0.5 * w * sin(.93 * iTime - 5.58) + w, 1.0 * h * sin(.82 * iTime - 3.04) + h)),
        RadialGradient(1.0, vec4(.482,.357,.784,.7), vec2(1.0 * w * sin(.45 * iTime - 2.70) + w, 0.5 * h * sin(.94 * iTime - 5.24) + h)),
        RadialGradient(1.0, vec4(.580,.702,.988,.7), vec2(1.0 * w * sin(.15 * iTime - 3.59) + w, 1.0 * h * sin(.96 * iTime - 5.11) + h)),
        RadialGradient(1.0, vec4(.161,.302,.827,.7), vec2(0.5 * w * sin(.35 * iTime - 4.73) + w, 0.5 * h * sin(.42 * iTime - 3.87) + h)),
        RadialGradient(1.0, vec4(.137,.467,.729,.7), vec2(0.5 * w * sin(.62 * iTime - 4.81) + w, 1.0 * h * sin(.19 * iTime - 1.17) + h)),
        RadialGradient(1.0, vec4(.114,.686,.925,.7), vec2(1.0 * w * sin(.58 * iTime - 5.13) + w, 0.5 * h * sin(.10 * iTime - 2.08) + h)),
        RadialGradient(0.5, vec4(.216,.412,.745,.7), vec2(1.5 * w * sin(.12 * iTime - 4.36) + w * 2., 0.5 * h * sin(.08 * iTime - 1.48) + h * 2.)),
        RadialGradient(0.6, vec4(.616,.792,.992,.7), vec2(1.5 * w * sin(.07 * iTime - 3.94) + w * 2., 0.6 * h * sin(.03 * iTime - 2.03) + h * 2.)),
        RadialGradient(0.5, vec4(.353,.906,.992,.7), vec2(1.5 * w * sin(.01 * iTime - 3.74) + w * 2., 0.5 * h * sin(.12 * iTime - 0.62) + h * 2.))
    );

    vec3 color = vec3(0.);

    for(int i = 0; i < gradients.length(); ++i)
    {
        color = mix(
            gradients[i].color.rgb,
            color,
            gradients[i].color.a * distance(gradients[i].point, fragCoord.xy) / ( fill * gradients[i].radius)
        );
    }

    fragColor = vec4(color, 1.);
}
