void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -1.0 + 2.0*fragCoord.xy / iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    //uv.x -= 0.7;

    const int max_iter = 1000;    
    float ReC = sin(iTime/5.0);
    float ImC = sin(iTime/4.0);

    //float ReC = -0.2+0.5*sin(iTime/3.0);
    //float ImC = -0.2+0.5*sin(iTime/5.0);

    float ReZ = uv.x;
    float ImZ = uv.y;

    float ReTmp;
    float ImTmp;

    int cur_iter = 0;

    for (int i=0; i<max_iter; i++){

        // (a+bi)^2 = a^2 + 2abi - b^2 
   		ReTmp = ReZ*ReZ - ImZ*ImZ + ReC;
        ImTmp = 2.0*ReZ*ImZ + ImC;

    	ReZ = ReTmp;
		ImZ = ImTmp;

        cur_iter = i;

        if (ReZ*ReZ+ImZ*ImZ > 4.0){
        	break;
        }
    }

	float intensity = 0.0;
    float abs2 = ReZ*ReZ+ImZ*ImZ;
    //if(abs2 < 4.0){
		intensity = float(cur_iter) / 20.0;
    //}else if (abs2 < 20.0){
	//	intensity = float(cur_iter) / 50.0;
    //}
    vec4 col=vec4(intensity, 0.0, 0.0, 1.0);

    fragColor = col;
}
