void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float t=iTime;

    float fx=0.0+25.*abs(sin(t)/10.)-mod(1.+t,25.);
    float s = 500.0; 
    float x = (floor(uv.x * 300.0) ) + (floor(uv.y * 300.0 )) * (t * 0.1);
	vec4 dtv = vec4(mod((mod(x, 2.0) + 1.0) * (mod(x, 0.0) + 0.0), 0.01)-0.005) * s;


    vec3 col = vec3(0.1, 0.1,0.1)+ dtv.xyz/fx;

    col.xyz += texture( iChannel0,uv  ).xyz;


    fragColor = vec4(col, 1.0);
}
