// modified from example by nikos papadopoulos, 4rknova / 2013

#ifdef GL_ES
precision highp float;
#endif

float metaball(vec2 dist)
{
	return 0.1 / (dist.x * dist.x + dist.y * dist.y);
}

vec3 _sample(in vec2 uv)
{
	float t0 = sin(iTime * 1.9) * .46;
	float t1 = sin(iTime * 2.4) * .49;
	float t2 = cos(iTime * 1.4) * .57;

	float r =
          metaball(uv + vec2(t0, t2)) *
		  metaball(uv - vec2(t0, t1)) *
		  metaball(uv + vec2(t1, t2));

	vec3 c = vec3(r,0.,0.);

	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy * 2. - 1.)
			* vec2(iResolution.x / iResolution.y, 1) * 1.25;

    vec3 col = _sample(uv);

    fragColor = vec4(clamp(col, 0., 1.), 1);
}
