void mainImage(out vec4 c,vec2 u){
    vec2 f = u / iResolution.xy - .5,
         m = max( abs(f), .1 ),
         b = ceil( 2.* vec2( f.y * 8.,
                             f / m.y + iTime ) );
    c += 1.5 * mod( b.y - b.x, 2. ) * m.y;}




