vec3 yellow = vec3(1.000,1.000,0.000);
vec3 red = vec3(1.000,0.000,0.000);
vec3 blue = vec3(0.000,0.000,1.000);
vec3 white = vec3(1.000,1.000,1.000);
vec3 black = vec3(0.000,0.000,0.000);


float createRectangle (vec2 uv, vec2 position, float size, vec2 range)
{    
    float value = step(position.x + size / range.x, uv.x) - step(position.x, uv.x);
   	value *= step(position.y + size * range.y, uv.y) - step(position.y, uv.y);

    return value;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    color = createRectangle (uv, vec2(0.0, 0.75), 0.4, vec2(2.5, 2.5)) * white; 
    color += createRectangle (uv, vec2(0.0 ,0.35), 0.1, vec2(0.62, 3.7)) * white;

    color += createRectangle (uv, vec2(0.0 ,0.0), 0.1, vec2(0.62, 3.2)) * blue;

    color += createRectangle (uv, vec2(.190, 0.35), 0.75, vec2(0.9, 2.5)) * red;
    color += createRectangle (uv, vec2(0.190 ,0.0), 0.21, vec2(0.295, 1.5)) * white;

    color += createRectangle (uv, vec2(0.925, 0.175), 0.1, vec2(1.1, 1.4)) * white;
    color += createRectangle (uv, vec2(0.925, 0.0), 0.1, vec2(1.1, 1.4)) * yellow;




	fragColor = vec4(color, 1.0);
}
