void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragColor = vec4(0);
    for (int i = 0; i < 200; i++) {
        vec2 uv = fragCoord.xy / iResolution.xy;
    	uv -= 0.5; //原点中心に
        uv.x *= iResolution.x / iResolution.y; //xyの幅統一

    	float t = iTime + float(i) * 0.1;
    	uv.x += 0.2 * sin(t * 2.); 
    	uv.y += 0.2 * sin(t * 3.); //原点位置を移動

    	float c = 1.0 / ( length(uv) * length(uv) ); //中心からの距離の2乗の逆数
    	c *= 0.0001; //適当

		fragColor += vec4(c, c * .2, c , 1.0); //色変更

    }
}
