// Gigatron France cpc 464 chr$(127)  


vec4 c1 = vec4(0.8, 0.8, 0.0, 1.0);
vec4 c2 = vec4(0.8, 0.8, 0.0, 1.0);
vec4 c3 = vec4(0.8, 0.8, 0.0, 1.0);
vec4 c4 = vec4(0.8, 0.8, 0.0, 1.0);
vec4 c5 = vec4(0.8, 0.8, 0.0, 1.0);

vec4 c0 = vec4(0.0, 0.0, 0.0, 1.0);



vec4 chr127(float lx, float ly, vec4 bc) {

 // firs line odd
    if (ly >= 0.9 )           { if (lx >= 0.0 && lx <=0.1) return c1; }
    if (ly >= 0.9 )           { if (lx >= 0.2 && lx <=0.3) return c2; }
    if (ly >= 0.9 )           { if (lx >= 0.4 && lx <=0.5) return c3; }
    if (ly >= 0.9 )           { if (lx >= 0.6 && lx <=0.7) return c4; }
    if (ly >= 0.9 )           { if (lx >= 0.8 && lx <=0.9) return c5; }
    // line 2 even .... 
   if (ly <= 0.9 && ly >=0.8) { if (lx >= 0.1 && lx <=0.2) return c1; }
   if (ly <= 0.9 && ly >=0.8) { if (lx >= 0.3 && lx <=0.4) return c1; } 
   if (ly <= 0.9 && ly >=0.8) { if (lx >= 0.5 && lx <=0.6) return c1; }
   if (ly <= 0.9 && ly >=0.8) { if (lx >= 0.7 && lx <=0.8) return c1; } 
   if (ly <= 0.9 && ly >=0.8) { if (lx >= 0.9 && lx <=1.0) return c1; }
   // odd
   if (ly <= 0.8 && ly >=0.7) { if (lx >= 0.0 && lx <=0.1) return c2; }
   if (ly <= 0.8 && ly >=0.7) { if (lx >= 0.2 && lx <=0.3) return c2; } 
   if (ly <= 0.8 && ly >=0.7) { if (lx >= 0.4 && lx <=0.5) return c2; }
   if (ly <= 0.8 && ly >=0.7) { if (lx >= 0.6 && lx <=0.7) return c2; } 
   if (ly <= 0.8 && ly >=0.7) { if (lx >= 0.8 && lx <=0.9) return c2; } 
   // even  
   if (ly <= 0.7 && ly >=0.6) { if (lx >= 0.1 && lx <=0.2) return c1; }
   if (ly <= 0.7 && ly >=0.6) { if (lx >= 0.3 && lx <=0.4) return c1; } 
   if (ly <= 0.7 && ly >=0.6) { if (lx >= 0.5 && lx <=0.6) return c1; }
   if (ly <= 0.7 && ly >=0.6) { if (lx >= 0.7 && lx <=0.8) return c1; } 
   if (ly <= 0.7 && ly >=0.6) { if (lx >= 0.9 && lx <=1.0) return c1; } 

   // odd
   if (ly <= 0.6 && ly >=0.5) { if (lx >= 0.0 && lx <=0.1) return c3; }
   if (ly <= 0.6 && ly >=0.5) { if (lx >= 0.2 && lx <=0.3) return c3; } 
   if (ly <= 0.6 && ly >=0.5) { if (lx >= 0.4 && lx <=0.5) return c3; }
   if (ly <= 0.6 && ly >=0.5) { if (lx >= 0.6 && lx <=0.7) return c2; } 
   if (ly <= 0.6 && ly >=0.5) { if (lx >= 0.8 && lx <=0.9) return c2; } 

   // even  
   if (ly <= 0.5 && ly >=0.4) { if (lx >= 0.1 && lx <=0.2) return c1; }
   if (ly <= 0.5 && ly >=0.4) { if (lx >= 0.3 && lx <=0.4) return c1; } 
   if (ly <= 0.5 && ly >=0.4) { if (lx >= 0.5 && lx <=0.6) return c1; }
   if (ly <= 0.5 && ly >=0.4) { if (lx >= 0.7 && lx <=0.8) return c1; } 
   if (ly <= 0.5 && ly >=0.4) { if (lx >= 0.9 && lx <=1.0) return c1; }  

   // odd
    if (ly <= 0.4 && ly >=0.3){ if (lx >= 0.0 && lx <=0.1) return c3; }
   if (ly <= 0.4 && ly >=0.3) { if (lx >= 0.2 && lx <=0.3) return c3; } 
   if (ly <= 0.4 && ly >=0.3) { if (lx >= 0.4 && lx <=0.5) return c3; }
   if (ly <= 0.4 && ly >=0.3) { if (lx >= 0.6 && lx <=0.7) return c2; } 
   if (ly <= 0.4 && ly >=0.3) { if (lx >= 0.8 && lx <=0.9) return c2; } 

   // even  
   if (ly <= 0.3 && ly >=0.2) { if (lx >= 0.1 && lx <=0.2) return c1; }
   if (ly <= 0.3 && ly >=0.2) { if (lx >= 0.3 && lx <=0.4) return c1; } 
   if (ly <= 0.3 && ly >=0.2) { if (lx >= 0.5 && lx <=0.6) return c1; }
   if (ly <= 0.3 && ly >=0.2) { if (lx >= 0.7 && lx <=0.8) return c1; } 
   if (ly <= 0.3 && ly >=0.2) { if (lx >= 0.9 && lx <=1.0) return c1; }   

   // odd
   if (ly <= 0.2 && ly >=0.1) { if (lx >= 0.0 && lx <=0.1) return c3; }
   if (ly <= 0.2 && ly >=0.1) { if (lx >= 0.2 && lx <=0.3) return c3; } 
   if (ly <= 0.2 && ly >=0.1) { if (lx >= 0.4 && lx <=0.5) return c3; }
   if (ly <= 0.2 && ly >=0.1) { if (lx >= 0.6 && lx <=0.7) return c2; } 
   if (ly <= 0.2 && ly >=0.1) { if (lx >= 0.8 && lx <=0.9) return c2; } 

   // even  
   if (ly <= 0.1 && ly >=0.0) { if (lx >= 0.1 && lx <=0.2) return c1; }
   if (ly <= 0.1 && ly >=0.0) { if (lx >= 0.3 && lx <=0.4) return c1; } 
   if (ly <= 0.1 && ly >=0.0) { if (lx >= 0.5 && lx <=0.6) return c1; }
   if (ly <= 0.1 && ly >=0.0) { if (lx >= 0.7 && lx <=0.8) return c1; } 
   if (ly <= 0.1 && ly >=0.0) { if (lx >= 0.9 && lx <=1.0) return c1; }  


  return bc;
}


vec4 bg = vec4(1.,0.,0.,0.0);

 void mainImage(out vec4 fragColor, in vec2 fragCoord) {

	vec2 p =  fragCoord.xy/iResolution.xy ;
    p.x *= iResolution.x/iResolution.y;


	vec4 bc = vec4(0.,0,0.3,0.9);

     p = fract(p*6.);


	fragColor = chr127(p.x,p.y, bc);    


}
